INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Barron''s AP Calculus', 571, 'Offers advice on preparing for and taking the advanced placement examinations in calculus, including a diagnostic tool, a review of the field, and eight sample tests with answers for both the Calculus AB and Calculus BC exams.', 'Barron''s Educational Series', 2018, 0, 1800, 0, 0, 'https://books.google.com/books/content?id=Z4hwkWDh5NIC&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
