INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Java Programming on Linux', 789, 'This detailed how-to book on using Java on a Linux operating system covers installing and enabling a Java runtime environment under Linux, Java development in Linux, running Java applications and applets, using Java with Linux-based Web servers, using Sun Components JCE and JAI in Linux, and more. The book points the way to a Web site with all the code from the book.', 'Que Publishing', 2000, 0, 1300, 0, 0, 'https://books.google.com/books/content?id=TIBGAAAAYAAJ&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Object-oriented Programming with Java', 790, 'Written to appeal to both novice and veteran programmers, this complete and well-organized guide to the versatile and popular object-oriented programming language Java shows how to use it as a primary tool in many different aspects of one''s programming work. It emphasizes the importance of good programming styleâparticularly the need to maintain an object''s integrity from outside interferenceâand helps users harness the power of Java in object-oriented programming to create their own interesting and practical every-day applications. Discusses the basics of computer systems, and describes the fundamental elements of the Java language, with complete instructions on how to compile and run a simple program. Introduces fundamental object-oriented concepts, and shows how simple classes may be defined from scratch. Explores Java''s exception-handling mechanism, and investigates Java''s interface facility (i.e., polymorphism). Covers all Java applications, including use of the Abstract Windowing Toolkit, graphical programming, networking, and simulation. Includes numerous exercises, periodic reviews, case studies, and supporting visuals. For those in the computer science industry.', 'Innopolis library', 2018, 0, 1900, 0, 0, 'https://books.google.com/books/content?id=uX5GAAAAYAAJ&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
