INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('PROGRAMMING IN C LANGUAGE', 820, 'This book presents programming in C Language as per the syllabus prescribed by the Directorate of Technical Education, Karnataka. This book fulfils the needs of II semester students of all branches of Diploma and in particular to the students of Computer Science and Engineering. Though cut for the syllabi, we have striven to elucidate the concepts and programming in a broader perspective. Thus deviating from mundane notes â like books. The subject matter is covered in fifteen chapters. A special chapter is devoted to mini projects in C. Chapter 1: Gives an introduction to computer programming. Chapter 2: Focuses on Algorithms and Flow charts. Chapter 3: Is on Elementary programming in C. Chapter 4: Discusses on Declarations assignments and variables. Chapter 5: Elaborates on Integer Arithmetic expressions. Chapter 6: Introduces some more data types in C. Chapter 7: Helps the reader to make decisions in C. Chapter 8: Explains the while and do while loops in C. Chapter 9: Delves on for loops. Chapter 10: Is all about printf and scanf functions. Chapter 11: Presents the aspects of function making in C. Chapter 12: Focuses on Arrays, strings and string functions in C. Chapter 13: Covers concepts of Structures and Unions. Chapter 14: Deals with C- preprocessor Chapter 15: Some mini projects in C have been presented in this chapter', 'Sapna Book House (P) Ltd.', 2018, 0, 1900, 0, 0, 'https://books.google.com/books/content?id=CCaIAwAAQBAJ&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('The C Answer Book', 821, 'Provides solutions to all exercises in Kernighan & Ritchie''s new ANSI C book. Ideal for use with K&R in any course on C. Careful study of this answer book will help understand ANSI C and enhance programming skills. Tondo & Gimpel describe each solution and completely format programs to show the logical flow.', 'Innopolis library', 1989, 0, 1700, 0, 0, 'https://books.google.com/books/content?id=-YkqAQAAMAAJ&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Programming In C', 822, 'It Introduces The C Programming Language To Both The Computer Novices And To The Advanced Software Engineers In A Well Organized And Systematic Manner. It Does Not Assume Any Preliminary Knowledge Of Computer Programming Of A Reader. It Covers Almost All Topics With Numerous Illustrative Examples And Well Graded Problems. Some Of The Chapters Such As Pointers, Preprocessors, Structures, Unions And The File Operations Are Thoroughly Discussed With Suitable Number Of Examples. The Source Code Of The Editor Package Has Been Included As An Appendix Of The Book.', 'New Age International', 1996, 0, 1200, 0, 0, 'https://books.google.com/books/content?id=CEc2WsZkxUsC&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
