INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Introduction to Assembly Language Programming', 841, 'This updated textbook introduces readers to assembly and its evolving role in computer programming and design. The author concentrates the revised edition on protected-mode Pentium programming, MIPS assembly language programming, and use of the NASM and SPIM assemblers for a Linux orientation. The focus is on providing students with a firm grasp of the main features of assembly programming, and how it can be used to improve a computer''s performance. All of the main features are covered in depth, and the book is equally viable for DOS or Linux, MIPS (RISC) or CISC (Pentium). The book is based on a successful course given by the author and includes numerous hands-on exercises.', 'Springer Science & Business Media', 2018, 0, 1100, 0, 0, 'https://books.google.com/books/content?id=olvLgmMZHB8C&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
