INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Assembly Language Programming for the Intel 80XXX Family', 844, 'Considers assembly programming language for the entire 80XXX family and deals with such topics as how addresses are computed, what the linker and loader do and why the 80386 is a significant advance. It includes end-of-section exercises, program diagrams and examples of working programs.', 'Macmillan Coll Division', 1991, 0, 2000, 0, 0, 'https://books.google.com/books/content?id=q_VCEpqcU2cC&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Guide to Assembly Language', 845, 'This book will enable the reader to very quickly begin programming in assembly language. Through this hands-on programming, readers will also learn more about the computer architecture of the Intel 32-bit processor, as well as the relationship between high-level and low-level languages. Topics: presents an overview of assembly language, and an introduction to general purpose registers; illustrates the key concepts of each chapter with complete programs, chapter summaries, and exercises; covers input/output, basic arithmetic instructions, selection structures, and iteration structures; introduces logic, shift, arithmetic shift, rotate, and stack instructions; discusses procedures and macros, and examines arrays and strings; investigates machine language from a discovery perspective. This textbook is an ideal introduction to programming in assembly language for undergraduate students, and a concise guide for professionals wishing to learn how to write logically correct programs in a minimal amount of time.', 'Springer Science & Business Media', 2018, 0, 1900, 0, 0, 'https://books.google.com/books/content?id=7arN6Ht59u4C&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
