INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('32/64-Bit 80x86 Assembly Language Architecture', 848, 'The increasing complexity of programming environments provides a number of opportunities for assembly language programmers. 32/64-Bit 80x86 Assembly Language Architecture attempts to break through that complexity by providing a step-by-step understanding of programming Intel and AMD 80x86 processors in assembly language. This book explains 32-bit and 64-bit 80x86 assembly language programming inclusive of the SIMD (single instruction multiple data) instruction supersets that bring the 80x86 processor into the realm of the supercomputer, gives insight into the FPU (floating-point unit) chip in every Pentium processor, and offers strategies for optimizing code.', 'Jones & Bartlett Publishers', 2018, 0, 1800, 0, 0, 'https://books.google.com/books/content?id=uxkz5ZBwOWEC&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('An Introduction to Assembly Language Programming and Computer Architecture', 849, 'This book is about two separate but related topics: assembly language programming and computer architecture. This is based on the notion that it is not possible to study computer architecture in any depth without some knowledge of assembly language programming and similarly, one of the reasons for studying assembly language programming is to gain an insight into how computers work - which naturally leads to their architecture. Introducing Assembly Language Programming and Computer Architecture is ideal for first year computer science or engineering students taking degree and diploma level courses. It will also be a useful reference for computer enthusiasts wishing to advance their knowledge and programming skills.', 'Itp - Media', 1996, 0, 1600, 0, 0, 'https://books.google.com/books/content?id=0JtQAAAAMAAJ&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
