INSERT INTO librarian.lmsinno_document (title, document_id, description, publisher, year, type, price, is_reference, copies_available, cover, date_added, is_bestseller) VALUES ('Assembly Language for Intel-based Computers', 851, 'This widely used, fully updated assembly language book provides basic information for the beginning programmer interested in computer architecture, operating systems, hardware manipulation, and compiler writing.Uses the Intel IA-32 processor family as its base, showing how to program for Windows and DOS. Is written in a clear and straightforward manner for high readability. Includes a companion CD-ROM with all sample programs, and MicrosoftÂ® Macro Assembler Version 8, along with an extensive companion Website maintained by the author. Covers machine architecture, processor architecture, assembly language fundamentals, data transfer, addressing and arithmetic, procedures, conditional processing, integer arithmetic, strings and arrays, structures and macros, 32-bit Windows programming, language interface, disk fundamentals, BIOS-level programming, MS-DOS programming, floating-point programming, and IA-32 instruction encoding.For embedded systems programmers and engineers, communication specialists, game programmers, and graphics programmers.', 'Prentice Hall', 2007, 0, 1800, 0, 0, 'https://books.google.com/books/content?id=9Pe6Ajhn40sC&printsec=frontcover&img=1&zoom=2', '2018-02-03', 0);
